import React, { useState, useEffect, useRef } from "react";
import FlashcardList from "./FlashcardList";
import './app.css';
import axios from "axios";
import FlashcardManager from "./FlashcardManager";
import Messages from "./Messages";
import Flashcards from "./Flashcards";

function App() {
  const [flashcards, setFlashcards] = useState([]);
  const [categories, setCategories] = useState([]);
  const [data, setData] = useState({ messages: [], flashcards: [] });
  const categoryEl = useRef();
  const amountEl = useRef();

  useEffect(() => {
    // Fetching categories from the local JSON file
    axios.get('./flashcards.json').then((res) => {
      setData(res.data);
    });

    return (
      <div>
        <h1>React App with JSON Data</h1>
        <Messages messages={data.messages} />
        <Flashcards flashcards={data.flashcards} />
      </div>
    );

    // Fetching flashcards from the local JSON file (flashcards.json)
    axios.get('./flashcards.json').then((res) => {
      setFlashcards(res.data.map((flashcard, index) => ({
        id: `${index}-${Date.now()}`,
        question: flashcard.question,
        answer: flashcard.answer,
        options: flashcard.options.sort(() => Math.random() - 0.5),
      })));
    });
  }, []);



  function decodeString(str) {
    const textArea = document.createElement(`textarea`);
    textArea.innerHTML = str;
    return textArea.value;
  }

  return (
    <>
      <form className="header">
        <div className="form-group">
          <label htmlFor="category">Category</label>
          <select id="category" ref={categoryEl}>
            {categories.map(category => (
              <option value={category.id} key={category.id}>{category.name}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="amount">Number of Questions</label>
          <input type="number" id="amount" min="1" step="1" defaultValue={10} ref={amountEl}></input>
        </div>
        <div className="form-group">
          <button className="btn">Generate</button>
        </div>
      </form>
      <div className="container">
        <FlashcardList flashcards={flashcards} />
      </div>
      <div className="App">
        <FlashcardManager />
      </div>
    </>
  );
}

export default App;
