import React, { useState, useEffect, useRef} from 'react';

export default function Flashcard({ flashcard }) {
  const [flip, setFlip] = useState(false);
  const [height, setHight] = useState('initial')

  const frontEl = useRef()
  const backEl = useRef()

  function setMaxHeight(){
    const frontHeight = frontEl.current.getBoundingClientReact().height
    const backHeight = backEl.current.getBoundingClientReact().height
    setHight(Math.max(frontHeight, backHeight, 100))
  }

useEffect(() => {

}, [flashcard.question,flashcard.answer, flashcard.options])

  return (
    <div
      className={`card ${flip ? 'flip' : ''}`}
      onClick={() => setFlip(!flip)}
    >
      <div className="front" ref = {frontEl}>
        {flashcard.question}
        <div className="flashcard-options">
          {flashcard.options ? (
            flashcard.options.map((option, index) => (
              <div key={index} className="flashcard-option">
                {option}
              </div>
            ))
          ) : (
            <div>No options available</div>
          )}
        </div>
      </div>
      <div className="back" ref = {backEl}>{flashcard.answer}</div>
    </div>
  );
}
